}

.card-toggle-btn {
    background: none;
    border: none;
    padding: 0.5rem;
    cursor: pointer;
    line-height: 1;
}

.card-toggle-btn svg {
    width: 20px;
    height: 20px;
    transition: transform 0.4s cubic-bezier(0.25, 1, 0.5, 1);
}

.card-content-wrapper {
    max-height: 0;
    overflow: hidden;
    transition: max-height 0.5s ease-in-out;
}

.card-content {
