css/components/write-for-us.css.content:
.write-for-us-page .page-header {
    padding-bottom: 1rem;
}

.write-content-wrapper {
    max-width: 900px;
    margin: 0 auto;
    position: relative;
}

.opportunities-section {
    margin-bottom: 1rem;
    position: relative;
    min-height: 240px;
}

.opportunities-section h2 {
    font-family: var(--font-serif);
    font-size: 2rem;
    text-align: center;
    margin: 0;
    color: var(--color-dark);
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    z-index: 2;
    background: var(--color-bg);
    padding: 0 1rem;
    border-radius: 8px;
}

.emoji-decoration {
    position: absolute;
    font-size: 3rem;
    transition: transform 0.3s ease;
    animation: dance 4s ease-in-out infinite;
    opacity: 0.8;
    cursor: grab;
    user-select: none;
    z-index: 10;
}

.emoji-decoration:hover {
    transform: scale(1.2);
    opacity: 1;
}

.emoji-decoration.dragging {
    cursor: grabbing;
    animation-play-state: paused;
    transform: scale(1.1);
}

.emoji-speech-bubble {
    position: absolute;
    background: var(--color-dark);
    color: white;
    padding: 8px 12px;
    border-radius: 12px;
    font-size: 0.9rem;
    white-space: nowrap;
    z-index: 1000;
    opacity: 0;
    transform: translateX(-50%) translateY(10px);
    transition: all 0.3s ease;
    pointer-events: none;
}

.emoji-speech-bubble[data-anger="moderate"] {
    background: #ff6b35;
    font-weight: 600;
}

.emoji-speech-bubble[data-anger="angry"] {
    background: #e74c3c;
    font-weight: 700;
    font-size: 1rem;
    animation: shake 0.5s ease-in-out infinite;
}

.emoji-speech-bubble[data-anger="furious"] {
    background: #c0392b;
    color: #fff;
    font-weight: 800;
    font-size: 1.1rem;
    text-shadow: 1px 1px 2px rgba(0,0,0,0.8);
    animation: shake 0.3s ease-in-out infinite;
}

.emoji-speech-bubble[data-anger="given-up"] {
    background: #7f8c8d;
    color: #bdc3c7;
    font-size: 1.2rem;
    animation: none;
}

.emoji-speech-bubble::after {
    content: '';
    position: absolute;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    width: 0;
    height: 0;
    border-left: 8px solid transparent;
    border-right: 8px solid transparent;
    border-top: 8px solid var(--color-dark);
}

.emoji-speech-bubble[data-anger="moderate"]::after {
    border-top-color: #ff6b35;
}

.emoji-speech-bubble[data-anger="angry"]::after {
    border-top-color: #e74c3c;
}

.emoji-speech-bubble[data-anger="furious"]::after {
    border-top-color: #c0392b;
}

.emoji-speech-bubble[data-anger="given-up"]::after {
    border-top-color: #7f8c8d;
}

.emoji-speech-bubble.show {
    opacity: 1;
    transform: translateX(-50%) translateY(0);
}

@keyframes shake {
    0%, 100% { transform: translateX(-50%) translateY(0) rotate(0deg); }
    25% { transform: translateX(-50%) translateY(0) rotate(-1deg); }
    75% { transform: translateX(-50%) translateY(0) rotate(1deg); }
}

@keyframes dance {
    0%, 100% { transform: translateY(0px) rotate(0deg) scale(1); }
    25% { transform: translateY(-10px) rotate(5deg) scale(1.1); }
    50% { transform: translateY(-5px) rotate(-3deg) scale(1.05); }
    75% { transform: translateY(-12px) rotate(7deg) scale(1.15); }
}

