export function formatAuthorNamesSummary(writers) {
    const names = writers.map(w => w.name);
    const count = names.length;

    if (count === 0) return '';
    if (count === 1) return names[0];
    if (count === 2) return `${names[0]} and ${names[1]}`;

    const remainingCount = count - 2;
    return `${names[0]}, ${names[1]}, and ${remainingCount} more`;
}

export function formatAuthorNamesFull(writers) {
    const names = writers.map(w => w.name);
    const count = names.length;

    if (count === 0) return '';
    if (count === 1) return names[0];
    if (count === 2) return `${names[0]} and ${names[1]}`;

    const allButLast = names.slice(0, -1).join(', ');
    const last = names.slice(-1);
    return `${allButLast}, and ${last}`;
}

js/lib/headerSearch.js.content:
import { performSearch } from './search.js';

function debounce(func, delay = 300) {
    let timeout;
    return (...args) => {
        clearTimeout(timeout);
        timeout = setTimeout(() => {
            func.apply(this, args);
        }, delay);
    };
}

export function initHeaderSearch() {
    const searchIconBtn = document.getElementById('search-icon-btn');
    const searchContainer = document.getElementById('header-search-container');
    const headerSearchForm = document.getElementById('header-search-form');
    const headerSearchInput = document.getElementById('header-search-input');
    const headerSearchClearBtn = document.getElementById('header-search-clear-btn');
    const headerSearchGoBtn = document.getElementById('header-search-go-btn');
    const headerSearchResults = document.getElementById('header-search-results');

    let selectedIndex = -1;

    const renderResults = (results) => {
        selectedIndex = -1;
        headerSearchResults.innerHTML = '';
        if (results.length === 0) {
            headerSearchResults.classList.remove('is-visible');
            return;
        }

        const fragment = document.createDocumentFragment();
        results.slice(0, 4).forEach(article => {
            const li = document.createElement('li');
            li.innerHTML = `
                <a href="#single-article-page/${article.id}">
                    <span class="search-result-title">${article.title}</span>
                    <span class="search-result-category">${article.category}</span>
                </a>
            `;
            fragment.appendChild(li);
        });
        headerSearchResults.appendChild(fragment);
        headerSearchResults.classList.add('is-visible');
    };

    const updateHighlight = () => {
        const items = headerSearchResults.querySelectorAll('li');
        items.forEach((item, index) => {
            item.classList.toggle('is-highlighted', index === selectedIndex);
        });
    };

    const handleSearch = async () => {
        const query = headerSearchInput.value.trim();
        headerSearchClearBtn.classList.toggle('visible', query.length > 0);
        headerSearchGoBtn.disabled = query.length === 0;

        if (query.length < 2) {
            renderResults([]);
            return;
        }

        const results = await performSearch(query);
        renderResults(results);
    };

    const debouncedSearch = debounce(handleSearch, 300);

    const openSearch = () => {
        document.body.classList.add('search-active');
        headerSearchInput.focus();
    };

    const closeSearch = () => {
        document.body.classList.remove('search-active');
        headerSearchInput.value = '';
        renderResults([]);
        headerSearchClearBtn.classList.remove('visible');
        if (headerSearchGoBtn) headerSearchGoBtn.disabled = true;
    };

    searchIconBtn.addEventListener('click', () => {
        if (document.body.classList.contains('search-active')) {
            closeSearch();
        } else {
            openSearch();
        }
    });

    headerSearchInput.addEventListener('input', debouncedSearch);

    headerSearchInput.addEventListener('keydown', (e) => {
        const items = headerSearchResults.querySelectorAll('li');
        if (items.length === 0) return;

        if (e.key === 'ArrowDown') {
            e.preventDefault();
            selectedIndex = (selectedIndex + 1) % items.length;
            updateHighlight();
        } else if (e.key === 'ArrowUp') {
            e.preventDefault();
            selectedIndex = (selectedIndex - 1 + items.length) % items.length;
            updateHighlight();
        } else if (e.key === 'Enter') {
            if (selectedIndex > -1) {
                e.preventDefault();
                items[selectedIndex].querySelector('a').click();
            }
        } else if (e.key === 'Escape') {
            headerSearchResults.classList.remove('is-visible');
        }
    });

    headerSearchClearBtn.addEventListener('click', () => {
        headerSearchInput.value = '';
        handleSearch();
        headerSearchInput.focus();
    });

    headerSearchForm.addEventListener('submit', (e) => {
        e.preventDefault();
        const query = headerSearchInput.value.trim();
        if (query) {
            location.hash = `#search/${encodeURIComponent(query)}`;
            closeSearch();
        }
    });

    headerSearchResults.addEventListener('click', (e) => {
        if (e.target.closest('a')) {
            setTimeout(closeSearch, 50);
        }
    });

    document.body.addEventListener('click', (e) => {
        if (!searchContainer.contains(e.target)) {
            headerSearchResults.classList.remove('is-visible');
        }
    });

    return closeSearch;
}

js/lib/modal.js.content:
import { getCombinedData } from '../api.js';

export function initModal() {
    const modalOverlay = document.getElementById('staff-modal');
    const modalCloseBtn = document.getElementById('modal-close-btn');
    const modalBodyContent = document.getElementById('modal-body-content');

    const createStaffModalHTML = (person) => `
        <img src="${person.image}" alt="${person.name}" class="modal-img">
        <div class="modal-bio">
            <h2>${person.name}</h2>
            <h4>${person.role}</h4>
            <p>${person.bio}</p>
        </div>
    `;

    const openModal = (person) => {
        modalBodyContent.innerHTML = createStaffModalHTML(person);
        modalOverlay.classList.add('active');
        document.body.style.overflow = 'hidden';
    };

    const closeModal = () => {
        modalOverlay.classList.remove('active');
        document.body.style.overflow = '';
    };

    document.getElementById('main-content').addEventListener('click', async (e) => {
        const card = e.target.closest('.staff-card');
        if (card) {
            const staffName = card.dataset.name;
            const { staff } = await getCombinedData();
            const person = staff.find(p => p.name === staffName);
            if (person) openModal(person);
        }
    });

    modalCloseBtn.addEventListener('click', closeModal);
    modalOverlay.addEventListener('click', (e) => {
        if (e.target === modalOverlay) closeModal();
    });

    return closeModal;
}

