js/lib/recommendations.js.content:
function calculateScore(articleA, articleB) {
    let score = 0;

    const commonTags = articleA.tags.filter(tag => articleB.tags.includes(tag));
    score += commonTags.length * 5;

    if (articleA.category === articleB.category) {
        score += 2;
    }

    return score;
}

export function getRecommendedArticles(currentArticle, allArticles, count = 4) {
    const recommendations = allArticles
        .filter(article => article.id !== currentArticle.id)
        .map(article => ({
            ...article,
            score: calculateScore(currentArticle, article)
        }))
        .filter(article => article.score > 0)
        .sort((a, b) => {
            if (b.score !== a.score) {
                return b.score - a.score;
            }
            return new Date(b.date) - new Date(a.date);
        });

    return recommendations.slice(0, count);
}

js/lib/search.js.content:
import Fuse from 'fuse.js';
import { getCombinedData } from '../api.js';

let fuseInstance = null;
let articlesForSearch = [];

function stripHtml(html) {
    if (!html) return "";
    const doc = new DOMParser().parseFromString(html, 'text/html');
    return doc.body.textContent || "";
}

export async function initializeSearch() {
    if (fuseInstance) return;

    const { articles } = await getCombinedData();

    articlesForSearch = articles.map(article => {
        const searchableContent = [];

        searchableContent.push(article.title);
        searchableContent.push(article.rawDescription);
        searchableContent.push(stripHtml(article.content));

        if (article.tags) searchableContent.push(article.tags.join(' '));
        if (article.categories) searchableContent.push(article.categories.join(' '));

        if (article.writers) {
            article.writers.forEach(writer => {
                searchableContent.push(writer.name);
                if (writer.role) searchableContent.push(writer.role);
                if (writer.bio) searchableContent.push(writer.bio);
            });
        }

        return {
            ...article,
            searchableText: searchableContent.join(' | ')
        };
    });

    const options = {
        keys: [
            { name: 'title', weight: 0.6 },
            { name: 'searchableText', weight: 0.4 }
        ],
        includeScore: true,
        minMatchCharLength: 2,
        threshold: 0.35,
        ignoreLocation: true,
    };

    fuseInstance = new Fuse(articlesForSearch, options);
}

export async function performSearch(query) {
    await initializeSearch();
    const results = fuseInstance.search(query);
    return results.map(result => result.item);
}

js/lib/sorting.js.content:
export function sortItems(items, sortBy) {
    const sortedItems = [...items];

    switch (sortBy) {
        case 'date-desc':
            sortedItems.sort((a, b) => new Date(b.date) - new Date(a.date));
            break;
        case 'date-asc':
            sortedItems.sort((a, b) => new Date(a.date) - new Date(b.date));
            break;
        case 'title-asc':
            sortedItems.sort((a, b) => a.title.localeCompare(b.title));
            break;
        case 'title-desc':
            sortedItems.sort((a, b) => b.title.localeCompare(a.title));
            break;
        default:
            sortedItems.sort((a, b) => new Date(b.date) - new Date(a.date));
            break;
    }

    return sortedItems;
}

