    }

}

css/components/contact/success-message.c.content:
/* the styles for sucess messages! */
#contact-success-message {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;

    display: flex;
    justify-content: center;
    align-items: center;
    text-align: center;

    background-color: var(--color-bg);

    opacity: 0;
    pointer-events: none;
    transition: opacity 0.3s ease-in-out;
}

#contact-form-container.form-submitted #contact-success-message {
    opacity: 1;
    pointer-events: auto;
}

#contact-success-message h3 {
    font-family: var(--font-serif);
    font-size: 1.75rem;
}

#contact-success-message p {
    color: var(--color-text-secondary);
    margin-bottom: 0;
}

css/components/forms/password-toggle.css.content:
.password-input-wrapper {
    position: relative;
}

.password-input-wrapper input {
    padding-right: 45px !important;
}

.password-toggle-btn {
