}

css/components/header/base.css.content:
/* base header styles */
.main-header {
    --header-height: clamp(5rem, 5rem + 3vw, 6rem);
    background-color: var(--color-bg); 
    border-bottom: 1px solid var(--color-border);
    position: sticky; 
    top: 0; 
    z-index: 1000;
    height: var(--header-height);
}

.nav-container { 
    height: 100%; 
    display: flex; 
    justify-content: space-between;
    align-items: center; 
    padding: 0 clamp(1rem, 4vw, 3rem);
    gap: 1.5rem;
    position: relative;
}

.logo { 
    display: flex;
    align-items: center;
    flex-shrink: 0;
    height: 100%;
    padding: 0.5rem 0;
}

.logo img {
    height: 100%;
    width: auto;
    display: block;
}

.header-center-content {
    flex-grow: 1; 
    display: flex;
    justify-content: center;
    min-width: 0;
}

.header-right {
    display: flex;
    align-items: center;
    flex-shrink: 0;
    gap: clamp(0.5rem, 2vw, 1rem);
}

.mobile-toggle { 
    display: none; 
    cursor: pointer;
    background: none;
    border: none;
    touch-action: manipulation;
    width: 44px;
    height: 44px;
    position: relative;
}

.bar {
    position: absolute;
    left: 10px;
    width: 24px;
    height: 3px;
    background-color: var(--color-dark);
    border-radius: 1px;
    transition: transform 0.5s cubic-bezier(0.34, 1.56, 0.64, 1);
}

.bar.top { transform: translateY(-5px); }
.bar.bottom { transform: translateY(5px); }

.main-header.nav-open .bar.top { transform: translateY(0) rotate(45deg); }
.main-header.nav-open .bar.bottom { transform: translateY(0) rotate(-45deg); }

#search-icon-btn {
    display: flex;
    align-items: center;
    justify-content: center;
    height: clamp(40px, 10vw, 44px);
    padding: clamp(0.6rem, 2vw, 0.7rem);
}

css/components/header/dropdown.css.content:
/* dropdown menu styles */
.dropdown { 
    position: relative; 
}

.dropdown::after {
    content: '';
    position: absolute;
    top: 100%;
    left: 0;
    right: 0;
    height: 12px;
    background-color: transparent;
}

.dropdown-menu {
    visibility: hidden;
    opacity: 0; 
    pointer-events: none;
    position: absolute; 
    top: 100%;
    left: 50%;
    background-color: var(--color-bg);
    padding: 0.5rem; 
    border-radius: 8px; 
    box-shadow: var(--shadow); 
    min-width: 180px;
    border: 1px solid var(--color-border);
    list-style: none;
    text-align: center;
    transform-origin: top center;
    transform: translateX(-50%) translateY(12px) scale(0.95);
    transition: opacity 0.2s ease-out, transform 0.2s ease-out, visibility 0s 0.2s; 
}

.dropdown:hover .dropdown-menu { 
    visibility: visible;
    opacity: 1; 
    pointer-events: auto;
    transform: translateX(-50%) translateY(12px) scale(1);
    transition: opacity 0.2s ease-out, transform 0.2s ease-out, visibility 0s 0s;
}

.dropdown-menu li { 
    margin: 0; 
}

.dropdown-menu a { 
    display: block; 
    padding: 0.75rem 1rem; 
    white-space: nowrap; 
    border-bottom: none; 
    border-radius: 4px;
}

.dropdown-menu a:hover { 
    background-color: var(--color-border); 
}

css/components/header/navigation.css.content:
.main-nav > ul { 
    display: flex; 
    list-style: none; 
    gap: 32px; 
}

.main-nav li { 
    margin: 0; 
}

.main-nav a { 
    font-size: 16px; 
    font-weight: 500; 
    border-bottom: 2px solid transparent; 
    padding: 4px 0; 
