js/components/common/Avatar.js.content:
import { generateUserGradient } from '../../lib/avatarGenerator.js';

export function Avatar({ 
    username, 
    customAvatar = null, 
    size = 'medium', 
    className = '', 
    attributes = {} 
}) {
    if (!username) {
        return '';
    }

    let style, content;
    if (customAvatar) {
        style = `background-image: url('${customAvatar}'); background-size: cover; background-position: center;`;
        content = '';
    } else {
        style = `background: ${generateUserGradient(username)};`;
        content = username.charAt(0).toUpperCase();
    }

    const attributeString = Object.entries(attributes)
        .map(([key, value]) => `${key}="${value}"`)
        .join(' ');

    const classes = `avatar avatar--${size} ${className}`.trim();

    return `<div class="${classes}" style="${style}" ${attributeString}>${content}</div>`;
}

export function AccountAvatar(user) {
    return Avatar({
        username: user.username,
        customAvatar: user.customAvatar || user.custom_avatar,
        size: 'large',
        className: 'account-avatar',
        attributes: { id: 'account-avatar' }
    });
}

export function CommentAvatar(user, isForm = false) {
    return Avatar({
        username: user.username || user.author_name,
        customAvatar: user.customAvatar || user.custom_avatar,
        size: 'medium',
        className: isForm ? 'comment-form-avatar' : 'comment-avatar'
    });
}

js/components/common/SocialShare.js.content:
export function SocialShare(article, options = {}) {
    const { variant = 'full' } = options;

    const currentUrl = encodeURIComponent(window.location.href);
    const title = encodeURIComponent(article.title);
    const description = encodeURIComponent(article.description);

    const shareLinks = [
        { name: 'Email', icon: 'email.svg', url: `mailto:?subject=${title}&body=${description}%0A%0A${currentUrl}` },
        { name: 'Facebook', icon: 'facebook.svg', url: `https://www.facebook.com/sharer/sharer.php?u=${currentUrl}` },
        { name: 'X (Twitter)', icon: 'twitter.svg', url: `https://twitter.com/intent/tweet?url=${currentUrl}&text=${title}` },
        { name: 'LinkedIn', icon: 'linkedin.svg', url: `https://www.linkedin.com/sharing/share-offsite/?url=${currentUrl}` },
        { name: 'Reddit', icon: 'reddit.svg', url: `https://reddit.com/submit?url=${currentUrl}&title=${title}` },
        { name: 'WhatsApp', icon: 'whatsapp.svg', url: `https://wa.me/?text=${title}%20${currentUrl}` }
    ];

    if (variant === 'minimal') {
        const minimalButtons = shareLinks.slice(0, 5).map(link => `
            <a href="${link.url}"
               target="_blank"
               rel="noopener noreferrer"
               class="social-share-button-minimal"
               title="Share on ${link.name}"
               aria-label="Share on ${link.name}">
                <img src="assets/icons/${link.icon}" alt="${link.name}" class="social-share-icon-minimal" aria-hidden="true">
            </a>
        `).join('');

        const copyLinkButton = `
            <button class="social-share-button-minimal copy-link-btn" title="Copy link" aria-label="Copy article link">
                <span class="copy-link-icon-wrapper">
                    <img src="assets/icons/link.svg" alt="Copy link" class="social-share-icon-minimal" aria-hidden="true">
                </span>
                <span class="copy-link-success-message">Copied!</span>
            </button>
        `;

        return `<div class="social-share-minimal">${copyLinkButton}${minimalButtons}</div>`;

    } else {
        const allShareLinks = [ ...shareLinks,
            { name: 'Telegram', icon: 'telegram.svg', url: `https://t.me/share/url?url=${currentUrl}&text=${title}` },
            { name: 'Bluesky', icon: 'bluesky.svg', url: `https://bsky.app/intent/compose?text=${title}%20${currentUrl}` }
        ];

        const fullButtons = allShareLinks.map(link => `
            <a href="${link.url}"
               target="_blank"
               rel="noopener noreferrer"
               class="social-share-button"
               aria-label="Share on ${link.name}">
                <img src="assets/icons/${link.icon}" alt="" class="social-share-icon" aria-hidden="true">
                <span>${link.name}</span>
            </a>
        `).join('');

        return `
            <div class="social-share-container">
                <h4 class="social-share-title">Share this Article</h4>
                <div class="social-share-buttons">
                    ${fullButtons}
                </div>
            </div>
        `;
    }
}

js/components/forms/ContactForm.js.content:
export function ContactForm() {
    return `
        <form id="contact-form" novalidate>
            ${FormField({
                id: 'contact-name',
                label: 'Name (required)',
                type: 'text',
                required: true,
                warningMessage: 'Please enter your name.'
            })}
            ${FormField({
                id: 'contact-email',
                label: 'Email (required)',
                type: 'email',
                required: true,
                warningMessage: 'Please enter a valid email address.'
            })}
            ${FormField({
                id: 'contact-website',
                label: 'Website',
                type: 'url',
                placeholder: 'https://example.com',
                warningMessage: 'Please enter a valid URL.'
            })}
            ${FormField({
                id: 'contact-message',
                label: 'Message (required)',
                type: 'textarea',
                rows: 5,
                required: true,
                warningMessage: 'Please enter your message.'
            })}
            <button type="submit" class="button-primary">Send Message</button>
        </form>
    `;
}

function FormField(options) {
    const {
        id,
        label,
        type = 'text',
        required = false,
        placeholder = '',
        rows,
        warningMessage
    } = options;

    const isTextarea = type === 'textarea';

    const input = isTextarea
        ? `<textarea id="${id}" name="${id.replace('contact-', '')}" rows="${rows}" ${required ? 'required' : ''}></textarea>`
        : `<input type="${type}" id="${id}" name="${id.replace('contact-', '')}" ${required ? 'required' : ''} ${placeholder ? `placeholder="${placeholder}"` : ''}>`;

    return `
        <div class="form-group">
            <label for="${id}">${label}</label>
            ${input}
            <span class="warning-message" data-warning-for="${id}">${warningMessage}</span>
        </div>
    `;
}

